        /// <returns></returns>
        [HttpGet("GetPeerInfo")]
        public async Task<string> GetPeerInfo()
        {
            string output = "";

            var nodeInfoList = Globals.Nodes.Select(x => new
            {
                x.Value.NodeIP,
                x.Value.NodeLatency,
                x.Value.NodeHeight,
                x.Value.NodeLastChecked
            })
            .ToArray();

            output = JsonConvert.SerializeObject(nodeInfoList);

            return output;

        }

